<div class="bg-img">
    <div class="content">
       <header class="text-dark">Login</header>
       <form  class="login-form" [formGroup]="loginForm">
          <div class="field">
             <span class="fa fa-user"></span>
             <input type="email" required placeholder="Email or Phone" formControlName="email">
          </div>
          <div class="field space">
             <span class="fa fa-lock"></span>
             <input type="password" class="pass-key" required placeholder="Password" formControlName="password">

          </div>

          <br><br>
          <div class="field" id="login">
            <input  type="submit" (click)="logindata()" value="LOGIN">
         </div>
          <div class="field" id="adminlogin">
            <input type="submit" (click)="logindataOfAdmin()" value="Admin Login">
         </div>
       </form>

       <div class="signup">
        <p class="text-dark">Don't have account?</p>  
          <a href="/register">Signup Now</a>
       </div>
       <div class="signup">
         <p class="text-dark">Wan't to go back?</p>

         <a href="">Home</a>
      </div>
    </div>
 </div>
