import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { LoanService } from '../services/loan.service';
import { Customer } from 'src/models/Customer';
import { Address } from 'src/models/Address';
import Swal from 'sweetalert2';
import { LoanData } from 'src/models/LoanData';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css'],
})
export class CustomerComponent {
  constructor(
    private activateRoute: ActivatedRoute,
    private fb: FormBuilder,
    private service: LoanService,
    private route: Router
  ) {}

  ngOnInit(): void {
    this.service.getSingleCustomer().subscribe((data) => {
      this.loanData = data;
    });
  }

  loanData: LoanData = {
    email: '',
    customerName: '',
    amount: '',
    term: '',
    approvalDate: 0,
    status: '',
  };

  appyForLoan() {
    Swal.fire({
      title: 'Do you want to save the changes?',
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: 'Save',
      denyButtonText: `Don't save`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        this.loanData.approvalDate = 0;
        this.loanData.status = 'Pending';

        this.service.applyLoan(this.loanData).subscribe((response) => {
          let appliedData = response;
          console.log(appliedData);
        });

        Swal.fire('Saved!', '', 'success');
      } else if (result.isDenied) {
        Swal.fire('Changes are not saved', '', 'info');
      }
    });
  }
}
<app-navbar></app-navbar>

<body>
  <section class="container">
    <header>Loan Application</header>

    <h2>Update Detaile If Required</h2>
    <form action="#" class="form">
      <div>
        <div class="input-box">
          <label>Loan Term</label>
          <input
            matInput
            [(ngModel)]="loanData.term"
            name="Term"
            type="number"
          />
        </div>
      </div>

      <div>
        <div class="input-box">
          <label>Loan Amount</label>
          <input
            matInput
            [(ngModel)]="loanData.amount"
            name="Amount"
            type="number"
          />
        </div>
      </div>

      <div class="input-box">
        <label>Full Name</label>
        <input
          matInput
          [(ngModel)]="loanData.customerName"
          name="customerName"
          type="text"
        />
      </div>

      <div class="input-box">
        <label>Email Address</label>
        <input matInput [(ngModel)]="loanData.email" name="email" type="text" />
      </div>

      <button (click)="appyForLoan()">Submit</button>
    </form>

    <br />
    <button>
      <a
        style="text-decoration: none; color: black; font-weight: bold"
        href="loanStatus"
        >Click here to check Status</a
      >
    </button>
  </section>
</body>
