import { Component } from '@angular/core';
import { LoanService } from '../services/loan.service';
import { Router } from '@angular/router';
import { LoanData } from 'src/models/LoanData';

@Component({
  selector: 'app-loan-status',
  templateUrl: './loan-status.component.html',
  styleUrls: ['./loan-status.component.css']
})
export class LoanStatusComponent {

  constructor(private loanService: LoanService,private router:Router) {}


  customerLoan:LoanData[]=[]

  loans: LoanData = {  email:'',customerName: '', amount:'',term:'',approvalDate:'',status:''};

 ngOnInit(): void {

   this.loanService.getCustomerLoanData().subscribe({
     next:data=>{
       this.loans=data;
     },
     error:e=>{
     alert("Failed due to Network Error");
     }
   });
 }
}
<div class="row my-4">
    <h2 class="fs-4 mb-3">All Customers</h2>
    <div class="col">
        <table class="table bg-white rounded shadow-sm table-hover rounded">
            <thead>
                <tr>
                    <th scope="col" width="50">#</th>
                    <th scope="col">Email</th>
                    <th scope="col">Customer Name</th>
                    <th scope="col">Amount</th>
                    <th scope="col">Term</th>
                    <th scope="col">approvalDate</th>
                    <th scope="col">status</th>
                </tr>
            </thead>
            <tbody>
                <!-- <tr *ngFor="let loan of loans; let i = index"> -->
                    <tr>
                    <!-- <th scope="row">{{i + 1}}.</th> -->
                    <td><strong>{{loans.email}}</strong></td>
                    <td><strong>{{loans.customerName}}</strong></td>
                    <td><strong>{{loans.amount}}</strong></td>
                    <td><strong>{{loans.term}}</strong></td>
                    <td><strong>{{loans.approvalDate}}</strong></td>
                    <td><strong>{{loans.status}}</strong></td>
                </tr>
                <!-- </tr> -->
            </tbody>
        </table>
    </div>
</div>
