import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
import { ServiceService } from '../services/service.service';

@Component({
  selector: 'app-register-interface',
  templateUrl: './register-interface.component.html',
  styleUrls: ['./register-interface.component.css']
})
export class RegisterInterfaceComponent implements OnInit{

  constructor(private fb:FormBuilder,private service:ServiceService,private route:Router){}
  ngOnInit(): void {
  }

  data:any;



  registrationForm = this.fb.group({
    customerName: ['',[Validators.required,Validators.minLength(5)]],
    email: ['',[Validators.required,Validators.email]],
    password: ['',[Validators.required,Validators.pattern(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[#$@!%&*?])[A-Za-z\d#$@!%&*?]{8,30}$/)]],
    mobileNo: ['',[Validators.required,Validators.pattern(/^\+?\d{1,4}?[-.\s]?\(?\d{1,3}?\)?[-.\s]?\d{1,4}[-.\s]?\d{1,4}[-.\s]?\d{1,9}$/)]],
  });

  get customerName(){return this.registrationForm.get("customerName")}
  get email(){return this.registrationForm.get("email")}
  get password(){return this.registrationForm.get("password")}
  get mobileNo(){return this.registrationForm.get("mobileNo")}


  verifydata(){
    this.service.registerCustomer({
      customerName:this.customerName?.value,
      email:this.email?.value,
      password:this.password?.value,
      mobileNo:this.mobileNo?.value,

    }).subscribe(data =>{
      console.log(data);

      Swal.fire({
        title: 'You are registered SuccessFull Now you Log In with your Email & Password',
        showClass: {
          popup: 'animate__animated animate__fadeInDown'
        },
        hideClass: {
          popup: 'animate__animated animate__fadeOutUp'
        }
      })
      this.registrationForm.reset();
      this.route.navigateByUrl("/login")

    },
    error=>{
      Swal.fire({
        icon: 'error',
        title: 'Oops...',
        text: 'Something went wrong!',
      })
    }) 
  }

}



 <div class="bg-img">
    <div class="content">
       <header class="text-dark">Sign Up</header>
       <form [formGroup]="registrationForm" (ngSubmit)="verifydata()" >

          <div class="field">
             <span class="fa fa-user"></span>
             <input type="text" matInput placeholder="User Name" formControlName="customerName">
          </div>
          <div class="field space">
            <span class="fa fa-user"></span>
            <input type="email" matInput placeholder="Email" formControlName="email">

         </div>
         <mat-error *ngIf="registrationForm.controls['email'].hasError('email')">Email Format is incorrect please check again !</mat-error>

          <div class="field space">
             <span class="fa fa-lock"></span>
             <input type="password" class="pass-key" required placeholder="Password" formControlName="password">
             <!-- <span class="show">SHOW</span> -->
          </div>
          <mat-error *ngIf="registrationForm.controls['password'].hasError('pattern')">Password pattern is incorrect please check again !</mat-error>
          <div class="field space">
            <span class="fa fa-user"></span>
            <input type="text" matInput placeholder="Phone Number" formControlName="mobileNo">
         </div>
         <mat-error *ngIf="registrationForm.controls['mobileNo'].hasError('pattern')">Phone No. pattern is incorrect please check again !</mat-error>

          <div class="field">
            <input type="submit" class="btn btn-danger" value="Submit" [disabled]="!registrationForm.valid" > 
             <!-- <input type="submit" value="REGISTER"> -->
          </div>
       </form>
       <div class="signup">
        <span class="text-dark"> Wan't to go back?</span>
         <a href="" style="border: 1px solid gray; border-radius: 10px;"> Home </a>
      </div>
    </div>
 </div>
