import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { LoanService } from 'src/app/services/loan.service';
import { LoanData } from 'src/models/LoanData';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-select-customer',
  templateUrl: './select-customer.component.html',
  styleUrls: ['./select-customer.component.css'],
})
export class SelectCustomerComponent {
  loanData: LoanData = {
    email: '',
    customerName: '',
    amount: '',
    term: '',
    approvalDate: 0,
    status: '',
  };

  constructor(
    private loanService: LoanService,
    private activateRoute: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.activateRoute.paramMap.subscribe((params) => {
      let email = params.get('email') ?? '';
      this.loanService.getSingleCustomerForLoan(email).subscribe((data) => {
        this.loanData = data;
      });
    });
  }

  updateData() {
    this.loanData.status = 'Approved';
    this.loanService
      .updateCustomerStatus(this.loanData.email, this.loanData)
      .subscribe({
        next: (data) => {
          Swal.fire({
            icon: 'success',
            title: 'Done',
            text: 'Loan Approved Successfully',
          });
        },
        error: (e) => {
          Swal.fire({
            icon: 'error',
            title: 'Oops...',
            text: 'Something went wrong!',
          });
        },
      });
    this.router.navigateByUrl('/admin');
  }

  removeData() {
    this.loanData.status = 'Rejected';
    this.loanService
      .updateCustomerStatus(this.loanData.email, this.loanData)
      .subscribe({
        next: (data) => {
          Swal.fire({
            icon: 'success',
            title: 'Done',
            text: 'Loan Rejected Successfully !',
          });
        },
        error: (e) => {
          Swal.fire({
            icon: 'error',
            title: 'Oops...',
            text: 'Something went wrong!',
          });
        },
      });
    this.router.navigateByUrl('/admin');
  }
}
<mat-progress-bar mode="indeterminate" color="accent"></mat-progress-bar>
<div class="navigation">
  <div>
    <h1>
      <a style="text-decoration: none; color: gray" href="/admin">
        <img
          src="assets\admin-image\infographic.gif"
          height="60px"
          width="70px"
          alt=""
        />
        Admin-Dashboard</a
      >
    </h1>
  </div>
</div>
<mat-progress-bar mode="query" color="accent"></mat-progress-bar>

<div class="container">
  <div class="row">
    <div class="display">
      <form action="" class="form">
        <div class="input-box">
          <label>Customer Name</label>
          <input
            matInput
            [(ngModel)]="loanData.customerName"
            name="restaurantName"
            type="text"
          />
        </div>

        <div class="input-box">
          <label>Loan Amount</label>
          <input
            matInput
            [(ngModel)]="loanData.amount"
            name="restaurantLocation"
            type="text"
          />
        </div>

        <div>
          <div class="input-box">
            <label>Loan Term in Years</label>
            <input
              matInput
              [(ngModel)]="loanData.term"
              name="rating"
              type="number"
            />
          </div>
        </div>

        <div>
          <div class="input-box">
            <label>Current Status</label>
            <input
              matInput
              [(ngModel)]="loanData.status"
              name="imageUrl"
              type="text"
            />
          </div>
        </div>
      </form>
    </div>

    <br /><br /><br />

    <div id="buttonPosition">
      <button (click)="removeData()" mat-fab extended class="bg-danger">
        <mat-icon>delete</mat-icon>
        Reject Loan
      </button>

      <button (click)="updateData()" mat-fab extended class="bg-success">
        <mat-icon>update</mat-icon>
        Approve Loan
      </button>
    </div>
  </div>
</div>
