import { Component } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { LoanService } from '../services/loan.service';
import { Customer } from 'src/models/Customer';
import { Address } from 'src/models/Address';
import Swal from 'sweetalert2';
import { LoanData } from 'src/models/LoanData';


@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.css']
})
export class CustomerComponent {

  constructor(private activateRoute: ActivatedRoute, private fb:FormBuilder,
    private service:LoanService,private route:Router){}

    urllink:string = "https://nregsmp.org/eService/images/User.png";
    selectFiles(file:any){
      if(file.target.files){
        const reader = new FileReader()
        reader.readAsDataURL(file.target.files[0])
        reader.onload = (event:any)=>{
          this.urllink = event.target.result
        }
      }

    }



  submitStatus:any;
 orders: Customer[] = [];
  add:Address = {houseNo:'',landMark:'',city:'',zipCode:''};
  customer: Customer = { customerName: '', email:'', password:'',mobileNo:'',profileDp:'',address:this.add};

  // loanData:LoanData={email:'',customerName: '', amount:'',term:'',approvalDate:0,status:"pending"};

  urlId:any

  getId(){
    let urllink:any = this.customer.profileDp;
    this.urlId = urllink;
    alert(this.urlId);
  }


  ngOnInit(): void {

    this.service.getSingleCustomer().subscribe(data =>{
       this.loanData = data;
      // this.urllink = this.customer.profileDp;
    })
  }

  loanData:LoanData={email:'',customerName: '', amount:'',term:'',approvalDate:0,status:"pending"};

  appyForLoan(){

    Swal.fire({
      title: 'Do you want to save the changes?',
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: 'Save',
      denyButtonText: `Don't save`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {
        this.loanData.approvalDate=0;
        this.loanData.status="Pending";

        this.service.applyLoan(this.loanData).subscribe(
          response => {
            let appliedData = response;

            // updtaData.customerName = this.loanData.customerName;
            // updtaData.email =
            // updtaData.profileDp = this.urllink;
            console.log(appliedData);
          }
        )

        Swal.fire('Saved!', '', 'success')
      } else if (result.isDenied) {
        Swal.fire('Changes are not saved', '', 'info')
      }
    })

  }


}
