import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LoanService } from 'src/app/services/loan.service';
import { Customer } from 'src/models/Customer';
import { LoanData } from 'src/models/LoanData';

@Component({
  selector: 'app-loan-request',
  templateUrl: './loan-request.component.html',
  styleUrls: ['./loan-request.component.css']
})
export class LoanRequestComponent {

  constructor(private loanService: LoanService,private router:Router) {}


  customerLoan:LoanData[]=[]

  loans: LoanData = {  email:'',customerName: '', amount:'',term:'',approvalDate:'',status:''};

 ngOnInit(): void {

   this.loanService.getCustomerLoanData().subscribe({
     next:data=>{
       this.loans=data;
     },
     error:e=>{
     alert("Failed due to Network Error");
     }
   });
 }

  submitLoanRequest(): void 
  {

    // // Call the loan service to submit the loan request
    // this.loanService.createLoan(this.loanData).subscribe((response) => {
    //   // Handle the response
    // });
  }

}

<div id="data" class="row my-4">
    <h2 class="fs-4 mb-3">All Customers</h2>
    <div class="col">
        <table class="table bg-white rounded shadow-sm  table-hover rounded">
            <thead>
                <tr>
                    <th scope="col" width="50">#</th>
                    <th scope="col">Email</th>
                    <th scope="col">Customer Name</th>
                    <th scope="col">Amount</th>
                    <th scope="col">Term</th>
                    <th scope="col">approvalDate</th>
                    <th scope="col">status</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let loan of loans let i = index" id="data" >
                    <th scope="row">{{i+1}}.</th>
                    <td><strong>{{loan.email}}</strong></td>
                    <td><strong>{{loan.customerName}}</strong> </td>
                    <td><strong>{{loan.amount}}</strong> </td>
                    <td><strong>{{loan.term}}</strong> </td>
                    <td><strong>{{loan.approvalDate}}</strong> </td>
                    <td><strong>{{loan.status}}</strong> </td>


                        <br>
                </tr>
            </tbody>
        </table>
    </div>
</div>
